import React from "react";
import classes from "./Modal.module.css";

const Modal = (props) => {

  let abilities = props.abilities.map((element) => {
    return <li key={Math.random()}>{element.ability.name}</li>;
  });

  let stats = props.stats.map((e) => {
    return [e.stat.name, e.base_stat];
  });

  let type1 = props.types[0].type.name;

  return (
    <div>
      <div className={classes.ModalButtons}>
        <button className={classes.backButton}
          onClick={() => {
            props.toggleModal(false);
          }}
        >
          Back
        </button>
      </div>
      <div className={classes.ModalContainer}>
        <div
          className={classes.Modal}
          style={{
            background: `linear-gradient(${props.backgroundColor[0]}, ${props.backgroundColor[1]})`,
          }}
        >
          <div className={classes.container}>
            <div className={classes.abilities}>
              <p>Abilities</p>
              <ul>{abilities}</ul>
            </div>

            <div className={classes.stats}>
              <p>Base Stats</p>
              <table>
                <tbody>
                  <tr>
                    <td>{stats[0][0]}</td>
                    <td>{stats[1][0]}</td>
                    <td>{stats[2][0]}</td>
                  </tr>
                  <tr>
                    <td>{stats[0][1]}</td>
                    <td>{stats[1][1]}</td>
                    <td>{stats[2][1]}</td>
                  </tr>
                  <tr>
                    <td>{stats[3][0]}</td>
                    <td>{stats[4][0]}</td>
                    <td>{stats[5][0]}</td>
                  </tr>
                  <tr>
                    <td>{stats[3][1]}</td>
                    <td>{stats[4][1]}</td>
                    <td>{stats[5][1]}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Modal;
