function Gradients(args) {
    let length = args.length;
    let color1, color2;
    switch(args[0].type.name){
        case "water":
            color1 = "#8cc4e2";
        case "grass":
            color1 = "#a8ff98";
            break;
        case "poison":
            color1 = "#d6a2e4";
            break;
        case "normal":
            color1 = "#dcdcdc";
            break;
    }
    return color1;
}

export default Gradients;
