import axios from 'axios';
import React, {useState, useEffect} from 'react';
import classes from './pokemon.module.css';
import Spinner from '../Spinner';
import Navbar from '../Navbar/Navbar';
import Modal from '../Modal/Modal';
const Pokemon = () => {
    const baseURL = 'https://pokeapi.co/api/v2/pokemon';
    const [currentPageURL, setCurrentPageURL] = useState(baseURL);
    const [nextPageURL, setNextPageURL] = useState('');
    const [previousPageURL, setPreviousPageURL] = useState('');
    const [pokemons, setPokemons] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setLoading(true);
        
        axios.get(currentPageURL)
        .then(res => {

            setPreviousPageURL(res.data.previous);
            setNextPageURL(res.data.next);
            setPokemons(res.data.results.map((pokemon, idx) => {

            let p = pokemon.url.split("/");
            let img_idx = p[p.length - 2];
            let source = "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + img_idx.toString() + ".png";
            let image = <img src={source} height="250" width="250"></img>;
            return (
                <div className={classes.gridItem}>
                    {image}
                    <p key={idx}>{pokemon.name}</p>
                </div>
                )
            }));
        }).then(() => setLoading(false))
    }, [currentPageURL])

    if (loading) return <Spinner/>; 

    const getPreviousPage = () => {
        if(previousPageURL){
            setCurrentPageURL(previousPageURL);
        }
        else{
            document.getElementById('pre').style.visibility = 'hidden';
        }
    }
    const getNextPage = () => {
        if(nextPageURL){
            setCurrentPageURL(nextPageURL);
        }
        else{
            setCurrentPageURL(baseURL)
        }

    }
    return (
        <div>
            <div style={{display:'inline'}}>
                <button>Open Modal</button>
            </div>
            {/* <Navbar/> */}
            <Modal/>
            {/* <button style={previousPageURL? {visibility:'visible'}: {visibility: 'hidden'}} 
            onClick = {getPreviousPage}>Previous</button>

            <button style={nextPageURL? {visibility:'visible'}: {visibility: 'hidden'}} 
            onClick = {getNextPage}>Next</button> */}
{/*       
            <div className={classes.pokemonContainer}>
                {loading?<Spinner/>:pokemons}
            </div> */}
            
   
        </div>
    )
}
export default Pokemon;
