import React from "react";
import classes from './Modal.module.css';

const Modal = (props) => {
    let abilities = props.abilities.map(element => {
        return <li key={props.name}>{element.ability.name}</li>
    });
    let stats = props.stats[0].map()
    console.log(props.stats)
    return (

        <div className={classes.ModalContainer}>

            <div className={classes.Modal} style={{ background: `linear-gradient(${props.backgroundColor[0]}, ${props.backgroundColor[1]})` }}>

                <div className={classes.title}>
                    {props.name}
                    <div>
                        <img src={props.pokemonImage} alt="..." height="250" width="250"></img>
                    </div>
                </div>
                <div className={classes.abilities}>
                    {abilities}
                </div>



                <div className={classes.ModalButtons}>
                    <button onClick={()=> {props.toggleModal(false)}}>Go Back</button>
                </div> 

            </div>
        </div>

    )
}
export default Modal;
