import axios from "axios";
import React, { useState, useEffect } from "react";
import classes from "../Pokemon/pokemon.module.css";
import Navbar from "../Navbar/Navbar";
import Modal from "../Modal/Modal";
import PokemonCard from "../PokemonCard/PokemonCard";
import Footer from "../Footer/Footer";
import Gradients from "../Gradients/Gradients";

const PokemonLazyLoad = () => {
  const baseURL = "https://pokeapi.co/api/v2/pokemon/";
  const [limit, setLimit] = useState(20);
  const [pokemons, setPokemons] = useState([]);
  const [isFetching, setIsFetching] = useState(false);

  const getPosts = (offset, limit) => {
    axios
      .get(baseURL + `?offset=${offset}&limit=${limit}`)
      .then((res) => {
        let requests = res.data.results.map((pokemon) => {
          return axios.get(pokemon.url);
        });
        return Promise.all(requests).then((data) => {
          setPokemons([...pokemons, data]);
        });
      })
      .catch((err) => console.log(err));
  };

  const getMorePosts= () => {
    getPosts(0, 10);
}

  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop !==
      document.documentElement.offsetHeight
    )
      return;
    setIsFetching(true);
  };
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    getPosts(0, 10);
  }, []);

  useEffect(() => {
    if (!isFetching) return;
    else getMorePosts();
  }, [isFetching]);

  pokemons.forEach((p, i) => {
    console.log(p[i])
  })

  return (
    <div>
      <div className={classes.pokemonContainer}>


      </div>
    </div>
  );
};

export default PokemonLazyLoad;
