import React from "react";
import Gradients from "../Gradients/Gradients";
import classes from './PokemonCard.module.css'

const PokemonCard = (props) => {
    let color = Gradients(props.types)
    if (props.type.length === 2) {
        color = Gradients(prop.stype[0].type.name, props.type[1].type.name, props.type.length);
    } else {
        color = Gradients(props.type[0].type.name, type[0].type.name, props.type.length);
    }
    return (
        <div style={{ background: `linear-gradient(${color[0]}, ${color[1]})` }} className={classes.gridItem} onClick={props.toggleModal}>
            <img src={props.image} width="250" height="250"></img>
            <p>{props.name}</p>
            <ul>
                {props.types.map(type => {
                    
                    return (<li>{type.type.name}</li>)
                })}
            </ul>
        </div>
    )
}

export default PokemonCard;
