import React from "react";
import classes from './Modal.module.css';

const Modal = (props) => {
    let abilities = props.abilities.map(element => {
        return <li key={props.name}>{element.ability.name}</li>
    });

    let stats = props.stats.map(e=>{
        return <li>{e.stat.name}: {e.base_stat}</li> ;
    });

    return (

        <div className={classes.ModalContainer}>

            <div className={classes.Modal} style={{ background: `linear-gradient(${props.backgroundColor[0]}, ${props.backgroundColor[1]})` }}>

                <div className={classes.title}>
                    <div className={classes.titleContent}>
                        {props.name}
                        <div>
                            <img src={props.pokemonImage} alt="..." height="250" width="250"></img>
                        </div>  
                        <div className={classes.heightWeight} style={{marginBottom:"2%"}}>
                            <strong>Height</strong> {props.heightWeight[0]/10 + "m"}
                        </div>
                        <div className={classes.heightWeight}>
                        <strong>Weight</strong> {props.heightWeight[1]/10 + "kg"}
                        </div>       
                    </div>
                </div>

                {/* <div className={classes.ModalButtons}>
                    <button onClick={()=> {props.toggleModal(false)}}>Go Back</button>
                </div>  */}

                <div className={classes.container}>
 
                    <div className={classes.abilities}>
                    <p>Abilities</p>
                        <ul>
                            {abilities}
                        </ul>
                    </div>
                    {/* <div className={classes.stats}>
                        {stats}
                    </div> */}
                </div>

            </div>
        </div>

    )
}
export default Modal;
