import React from "react";
import classes from "./Modal.module.css";

const Modal = (props) => {

  let abilities = props.abilities.map((element) => {
    return <li key={Math.random()}>{element.ability.name}</li>;
  });

  let stats = props.stats.map((e) => {
    return [e.stat.name, e.base_stat];
  });

  let type1 = props.types[0].type.name;

  return (
    <div>
      <div className={classes.ModalButtons}>
        <button className={classes.backButton}
          onClick={() => {
            props.toggleModal(false);
          }}
        >
          Back
        </button>
      </div>
      <div className={classes.ModalContainer}>
        <div
          className={classes.Modal}
          style={{
            background: `linear-gradient(${props.backgroundColor[0]}, ${props.backgroundColor[1]})`,
          }}
        >
          <div className={classes.imgContainer}>
            <div className={classes.translucent}></div>
            <div className={classes.name}>{props.name}</div>
            <img className={classes.img} src={props.pokemonImage} alt="..."></img>
            <div>
              <p className="">Height {props.height/10} m</p>
              <p>Weight {props.weight/10} kg</p>
            </div>
          </div>
          <div className={classes.statsContainer}>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Modal;
