import axios from 'axios';
import React, {useState, useEffect} from 'react';
import classes from './pokemon.module.css';
import Navbar from '../Navbar/Navbar';
import Modal from '../Modal/Modal';
// import SpinnerModal from '../SpinnerModal/SpinnerModal';


const Pokemon = () => {
    const baseURL = 'https://pokeapi.co/api/v2/pokemon';
    const [currentPageURL, setCurrentPageURL] = useState(baseURL);
    const [nextPageURL, setNextPageURL] = useState('');
    const [previousPageURL, setPreviousPageURL] = useState('');
    const [pokemons, setPokemons] = useState([]);
    const [modal, setModal] = useState(false);
    const [selectedPokemon, setSelectedPokemon] = useState('');
    const [selectedPokemonImageURL, setSelectedPokemonImageURL] = useState(''); 
    const [selectedPokemonAbilities, selectedPokemonSetAbilities] = useState([]);
    // const [type, setType] = useState([]);
    useEffect(() => {
        window.scrollTo(0, 0);

        axios.get(currentPageURL)
        .then(res => {
            setPreviousPageURL(res.data.previous);
            setNextPageURL(res.data.next);
            return res.data
        })
        .then(data=>{
    
            let types = data.results.map((pokemon, idx) => {

                let p = pokemon.url.split("/");
                let img_idx = p[p.length - 2];
                let source = "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + img_idx.toString() + ".png";
                let getTypesURL = baseURL + "/" + img_idx.toString();
                return [img_idx, source, getTypesURL, pokemon.name];
                }
            )
            return types;
        })
        .then(data=>{
            let URLs = data.map(element =>  element[2])
            let requests = URLs.map((url) => {
                return axios.get(url);
              });   

            Promise.all(requests)
            .then((res) => {
                let types = res.map(e=>{
                    return e.data.types
                })
                let types1 = types.map(e=>{
                    return e.type
                })
                console.log(types1);
                setPokemons(data.map(element => {
                    let image = <img src={element[1]} height="250" width="250" alt="..."></img>;
                    return (
                        <div className={classes.gridItem} onClick={()=>{toggleModalHandler(true, element[3], element[1])}}>
                            {image}
                            {<p>{element[3]}</p>}

                        </div>
                        )
                    }));
             }).catch((err) => {
                console.log(err)
             });
        })


    }, [currentPageURL])

    const toggleModalHandler = (isSet, pokemonName, pokemonImage) => {

        setSelectedPokemon(pokemonName);
        setSelectedPokemonImageURL(pokemonImage); 
        setModal(isSet);
        axios.get("https://pokeapi.co/api/v2/pokemon/" + pokemonName)
        .then(res => selectedPokemonSetAbilities(res.data.abilities))
        .catch(err => console.log(err));
       
    }
    const getPreviousPage = () => {
        if(previousPageURL) setCurrentPageURL(previousPageURL);
        else document.getElementById('pre').style.visibility = 'hidden';
    }
    const getNextPage = () => {
        if(nextPageURL) setCurrentPageURL(nextPageURL);
        else setCurrentPageURL(baseURL);
    }


    return (
        <div>   

            <Navbar/>
            <div className={classes.pokemonContainer}>
                {!modal && pokemons}
            </div>   

            {modal && 
                <Modal toggleModal={toggleModalHandler} name={selectedPokemon} 
                pokemonImage={selectedPokemonImageURL}
                abilities={selectedPokemonAbilities}/>}
            
            <footer className={classes.NavButtons}>
                <div>
                    {!modal && 
                    <button id="pre" className={classes.Previous}
                    style={previousPageURL? {visibility:'visible'}: {visibility: 'hidden'}}
                    onClick = {getPreviousPage}>
                        Previous
                    </button>}
                </div>
                <div>
                    {!modal && 
                    <button id="next" className={classes.Next}
                    style={nextPageURL? {visibility:'visible'}: {visibility: 'hidden'}} 
                    onClick = {getNextPage}>
                        Next
                    </button>}   
                </div>
            </footer>  

        </div>
    )
}
export default Pokemon;
