import axios from 'axios';
import React, {useState, useEffect} from 'react';
import classes from './pokemon.module.css';
import Navbar from '../Navbar/Navbar';
import Modal from '../Modal/Modal';
import PokemonCard from '../PokemonCard/PokemonCard';
import Footer from '../Footer/Footer';
import Gradients from "../Gradients/Gradients";

const Pokemon = () => {
    const baseURL = 'https://pokeapi.co/api/v2/pokemon';
    const [currentPageURL, setCurrentPageURL] = useState(baseURL);
    const [nextPageURL, setNextPageURL] = useState('');
    const [previousPageURL, setPreviousPageURL] = useState('');
    const [pokemons, setPokemons] = useState([]);
    const [modal, setModal] = useState(false);
    const [selectedPokemon, setSelectedPokemon] = useState('');
    const [selectedPokemonImageURL, setSelectedPokemonImageURL] = useState(''); 
    const [selectedPokemonAbilities, selectedPokemonSetAbilities] = useState([]);
    const [backgroundColor, setBackgroundColor] = useState('');
    const [stats, setStats] = useState([]);
    const [heightWeight, setHeightWeight] = useState([]);
    const [pageURLs, setPageURLs] = useState([]);
    const [selectedPokemonData, setSelectedPokemonData] = useState([]);
    // selectedPokemonData will store: 
     name, imageURL, Abilities, stats,
    function fetchDetails() {
        axios.get(currentPageURL)
        .then(res => {
            setPageURLs([res.data.previous, res.data.next])
            let requests = res.data.results.map(pokemon=>{
                return axios.get(pokemon.url)
            })
            return Promise.all(requests)
            .then(data=>{
                setPokemons(data);
            })
        })
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        fetchDetails();
    }, [currentPageURL])

    const toggleModalHandler = (isSet, pokemonName, pokemonImage, backgroundColor) => {

        setSelectedPokemon(pokemonName);
        setSelectedPokemonImageURL(pokemonImage); 
        setBackgroundColor(backgroundColor);
        setModal(isSet);
        // The above setStates are batched together.
        axios.get("https://pokeapi.co/api/v2/pokemon/" + pokemonName)
        .then(res => {
            selectedPokemonSetAbilities(res.data.abilities);
            setStats(res.data.stats);
            setHeightWeight([res.data.height, res.data.weight])
        }
        )
        .catch(err => console.log(err));
       
    }
    const getPreviousPage = () => {
        if(pageURLs[0]) setCurrentPageURL(pageURLs[0]);
        else document.getElementById('pre').style.visibility = 'hidden';
    }
    const getNextPage = () => {
        if(pageURLs[1]) setCurrentPageURL(pageURLs[1]);
        else setCurrentPageURL(baseURL);
    }
    console.log(pokemons);
    return (
        <div>   
            <Navbar/>
            <div className={classes.pokemonContainer}>
                {!modal && pokemons.map((record, i)=>{
                    return <PokemonCard 
                    toggleModal={()=>
                    toggleModalHandler(true, record.data.name, 
                        record.data.sprites.front_default, 
                        Gradients(record.data.types[0].type.name, record.data.types[1].type.name, record.data.types.length))}
                    name={record.data.name} 
                    image={record.data.sprites.front_default} 
                    types={record.data.types}/>
                })}
            </div>   

            {modal && 
                <Modal toggleModal={toggleModalHandler} 
                backgroundColor={backgroundColor}
                name={selectedPokemon} 
                pokemonImage={selectedPokemonImageURL}
                abilities={selectedPokemonAbilities}
                stats = {stats}
                heightWeight={heightWeight}
                />}

            <Footer 
                modal={modal}
                previousPageURL={pageURLs[0]}
                nextPageURL={pageURLs[1]}
                goNext={getNextPage}
                goPrev={getPreviousPage}
            />
            
        </div>
    )
}
export default Pokemon;
